<table>
<tr bgcolor="#CCCCCC">
<td>EBU Rank</td>
<td>User</td>
<td>Number of Writeups</td>
<td>Median Rep</td>
<td>MNFP</td>
</tr>
[%
  #return 'Closed for Now. Message [JayBonci] if you\'d like it opened to do some stats work. Thanks<br><br>';

  my $str = '';

  my $csr = $dbh->prepare("SELECT node_id FROM user left join node on node.node_id=user.user_id ORDER BY experience DESC limit 54");

  $csr->execute();

  my $ebucount=0;

  while(my $row = $csr->fetchrow_hashref())
  {

    my $usrnode = getNodeById($$row{node_id});

    next if($$usrnode{title} eq 'dbrown');
    next if($$usrnode{title} eq 'nate');
    next if($$usrnode{title} eq 'ShadowLost');
    next if($$usrnode{title} eq 'Webster 1913');
    next if($$usrnode{title} eq 'EDB');

    my $usrvars = getVars($usrnode);
    my $nwus = int($$usrvars{numwriteups} / 2);

  my $med = -1;
  my $nsofar = $DB->sqlSelect("count(*)", "writeup, node", "writeup_id = node_id AND author_user=$$usrnode{node_id} AND reputation <0");
 
  while($nsofar < $nwus)
  {
    $med++;

    $nsofar += $DB->sqlSelect("count(*)", "writeup, node", "writeup_id = node_id AND author_user=$$usrnode{node_id} AND reputation=$med");

  }

    $str.='<tr>';
    $str.='<td>'.++$ebucount.'</td>';
    $str.='<td>'.linkNode($usrnode, $$usrnode{title}).'</td>';
    $str.='<td>'.$$usrvars{numwriteups}.'</td>';
    $str.='<td>'.$med.'</td>';
    $str.='<td>'.($med+1)*$$usrvars{numwriteups}.'</td>';

    $str.='</tr>';     

  }

  return $str;

%]
</table>
