node.code:
[%
return 'Hey, don\'t press that button!' unless(isGod($USER));

my $initial_wu = '';

if(defined($query->param('writeup_id')))
{
     $initial_wu = $query->param('writeup_id');
}

if(defined($query->param('old_writeup_id')))
{
     $initial_wu = $query->param('old_writeup_id');
}

my $str ='<form method="post" enctype="application/x-www-form-urlencoded">
