node.code:
[%
return "";


my $node_id=$query->param('olde2nodeid');
return 'huh?  it\'s empty' unless $node_id;


sub reinsertCorpse {
    my ($N) = @_;

    my @kids;
    if ($$N{group}) {
        foreach (@{ $$N{group} }) {
            my $KID = resurrectNode($_);
            push @kids, reinsertCorpse($KID);
        }
    }

    my $author = $$N{author_user};
    delete $$N{author_user};
    my $title = $$N{title};
    delete $$N{title};
    my $type = getNodeById $$N{type_nodetype};
    delete $$N{type_nodetype};
    delete $$N{group} if exists $$N{group};

    my $A = getNodeById($author);
    $A = getNode('nate','user') if not $A or $$A{type}{title} ne 'user';

    my $id = insertNode($title, $type, $A, $N);

    insertIntoNodegroup($id, $author, \@kids) if @kids;
    #print "sucessfully inserted \"$title\" as node_id $id\n";
    $id;

}


sub resurrectNode {
    my ($node_id) = @_;

    my $N = $DB->sqlSelectHashref("*", 'tomb', "node_id=".$dbh->quote("$node_id"));
    return unless $N;


    my $DATA = eval("my ".$$N{data});

    @$N{keys %$DATA} = values %$DATA;

    delete $$N{data};
    delete $$N{killa_user};
    delete $$N{node_id};

    $N;
}

my $N = resurrectNode($node_id);

return "it has failed.  I am sorry, child" unless $N;
my $id = reinsertCorpse($N);

return "Reinsertion Failed" unless($id);

return "<h1>".linkNode($id, "IT LIVES!")."</h1>";

#$query->param('node_id', $id);

%]
