[%
my $csr = $Everything::dbh->prepare('select node_id,title,experience,vars from user left join node on node_id=user_id left join setting on setting_id=user_id order by experience DESC limit 54');

return 'Ack! Something\'s broken...' unless($csr->execute);

# Skip these users
my $skip = {
	'dbrown'=>1,
	'nate'=>1,
	'Webster 1913'=>1,
	'ShadowLost'=>1,
	'EDB'=>1
};
my $uid = getId($USER) || 0;
my $isMe;
my $node;
my $str = '';
my $step = 0;
my $color;
my $range = { 'min' => 130, 'max' => 230, 'steps' => 50 };
my $curr;
my $lvlexp = getVars(getNode('level experience', 'setting'));
my $lvlttl = getVars(getNode('level titles', 'setting'));
my $lvl;
use Everything::Experience;

while($node = $csr->fetchrow_hashref) {
  
	next if(exists $$skip{$$node{title}});

	$isMe = $$node{node_id}==$uid;

	$lvl = getLevel($node);
	my $V = getVars($node);

	# if the person doesn't like gradients, let's not give 'em gradients! --fw
	if ($$THEME{'nogradlinks'} == 1) { 
		if ($color eq ($$THEME{'oddRowColor'})) { $color = $$THEME{'bodyBgcolor'}; }
		else					{ $color = $$THEME{'oddRowColor'}; }
	}
	else {
		$curr = $$range{max} - (($$range{max} - $$range{min})/$$range{steps}) * $step;
		$curr = sprintf('%02x', $curr);
		$color = '#' . $curr . $curr . $curr;
		++$step;
	}
	$str .= '<tr bgcolor="' . $color . '"><td>' . ($isMe ? '<strong>' : '') . linkNode($node) . ($isMe ? '</strong>' : '') . '</td><td>'.$$node{experience}.'</td><td>'.$$V{numwriteups}.'</td><td>'.$$lvlttl{$lvl}.'</td><td>'.$lvl."</td></tr>\n";
}

$str;
%]


[%
return ''; # By number of writeups.  Turn off for now.
my $type1 = getId(getType('writeup'));
   my $type2 = getId(getType('superdoc'));
   my $csr = $Everything::dbh->prepare("
	select DISTINCT author_user, count(author_user) 
	from node
        where type_nodetype=$type1 OR type_nodetype=$type2
	group by author_user");
$csr->execute;
my %usrHash;
my %hide;

# Remove these users from the list
$hide{getId(getNode('Webster 1913', 'user'))} = 1;
$hide{getId(getNode('ShadowLost', 'user'))} = 1;

while (my ($user, $count) = $csr->fetchrow) {
	# skip these users
	next if(exists $hide{$user});
	$usrHash{$user} = $count;
}
$csr->finish;
delete $usrHash{''};
my @author = sort {$usrHash{$b} <=> $usrHash{$a}} (keys (%usrHash));
use Everything::Experience;
my ($min, $max, $steps) = (160, 255, 50);
 
my $count = 0;
my $num;
my $str = '';
while ($count < $steps) { 
	unless($author[$count])
        {
            $count++;
            next;
        }
        $num = sprintf "%lx", ($max-((($max-$min)*$count) / $steps));	
	my ($u) = getNodeWhere({node_id=>$author[$count]},getType('user'));
        $str .= "<tr bgcolor='".$num.$num."CC'><td>
	". linkNode ($author[$count], $$u{title}) ."
	</td><td align=center>
	$usrHash{$author[$count]} </td><td>Level: "
        .getLevel($u)."</td></tr>\n"; 
        $count++; 
}

if (getId($USER) ne $HTMLVARS{guest_user} and  
  $usrHash{getId($USER)}<$usrHash{$author[$count-1]}) {

   $str.= "<tr bgcolor=\"#ff6666\"><td>"
  .linkNode($USER)
  ."</td><td align=center>"
  .$usrHash{getId($USER)}
  ."</td><td>Level: "
        .getLevel($USER).'</td></tr>'; 
}
$str;
%]
