node.code:
[% 
my $UID = getId($USER) || 0;
my $isGuest = ($UID==$HTMLVARS{guest_user}) || ($$USER{title} eq 'Guest User') || ($UID==0);
my $r = rand();
my @phrase = (
	'Nodes your '.($r<0.5?($r<0.25?'cousin':'sibling'):($r>0.75?'grandma':'grandpa')).' would have liked:',
	'After stirring Everything, these nodes rose to the top:',
	'Look at this mess '.($isGuest?'the Death Borg':$$USER{title}).' made!',
	'Just another sprinkling of '.($r<0.5?'indeterminacy':'randomness'),
	'The '.($r<0.5?'best':'worst').' nodes of all time:',
	($r<0.5?'Drink up!':'Food for thought:'),
	'Things '.($isGuest?'you':$$USER{title}).' could have written:',
	'What '.($isGuest?'you are':$$USER{title}.' is').' reading:',
	'Nodes to '.($r<0.5?'live by':'die for').':',
	'Little presents from the Node Fairy:',
);

my $str;
$str.='<em>'.$phrase[rand(int(@phrase))]."</em><br />\n";

my $len = 20;
foreach(1..12) { 
	my $RN = getNodeById(getRandomNode() ,'light' );
	my $node_title = $$RN{'title'};
	$node_title =~ s/(\S{$len})\S{4,}/$1.../go;
	$str .= linkNode($RN, $node_title, {lastnode_id=>0}) . "<br />\n";
}

$str;
%]
