node.context:
[%
$query->start_form(-method=>'POST', action=>$ENV{script_name}, name=>'pagebody', id=>'pagebody') .
$query->hidden('displaytype') .
$query->hidden('node_id', getId($NODE));
%]
[%
if ($NODE && $$NODE{type} && $query->param('displaytype') eq 'edit') {
  my $type = $$NODE{type}{title};
  return htmlcode('verifyRequestForm', "edit_$type");
} else {
  return "";
}
%]
