node.doctext:
Click the +/- button by the nodelet name to
collapse/uncollapse it. Then <a href="http://www.everything2.com/index.pl?node_id=1450923&displaytype=display">refresh</a>
the page, and notice the collapse/uncollapse
state remains (unless you've disabled cookies).
<br><br>
-- [rdude]
<br><br>
Other documentation may be found [edev: Collapsible Nodelets|here].
<br><br>

<!-- BEGIN common collapse script -->
<script language="javascript">
<!--
	// Save a cookie
	function SetNodeletCookie (name,value,path,domain,secure) {
		var todays_date = new Date();
		var expires = new Date(todays_date.getTime() +
		  (14 * 86400000)); // 2 weeks from now

	    document.cookie = name + "=" +escape(value) +
	        ( (expires) ? ";expires=" + expires.toGMTString() : "") +
	        ( (path) ? ";path=" + path : "") + 
	        ( (domain) ? ";domain=" + domain : "") +
	        ( (secure) ? ";secure" : "");
	}
	
	// Eat that cookie
	function GetNodeletCookie (name) {
	    var start = document.cookie.indexOf(name+"=");
	    var len = start+name.length+1;
	    if ((!start) && (name != document.cookie.substring(0,name.length))) return null;
	    if (start == -1) return null;
	    var end = document.cookie.indexOf(";",len);
	    if (end == -1) end = document.cookie.length;
	    return unescape(document.cookie.substring(len,end));
	}
	
	// Switch nodelet from hidden <-> displayed
	function ToggleNodeletState (NodeletName) {
		var Nodelet = document.getElementById(NodeletName);
		var NodeletButton = document.getElementById(NodeletName + 'Button');
		
		if (Nodelet.style.display == '') {
			Nodelet.style.display = 'none';
			
			if (NodeletButton.tagName == 'SPAN') {
				NodeletButton.innerText = '+';
				NodeletButton.innerHTML = '+';
			}
			
			SetNodeletCookie(NodeletName, 'none');
		}
		else
		{
			Nodelet.style.display = '';
			
			if (NodeletButton.tagName == 'SPAN') {
				NodeletButton.innerText = '-';
				NodeletButton.innerHTML = '-';
			}
			
			SetNodeletCookie(NodeletName, '');
		}
	}
	
	// Hide nodelets as dictated by cookies
	function CheckNodeletState (NodeletName) {
		if (GetNodeletCookie(NodeletName) == 'none') {
			ToggleNodeletState(NodeletName);
		}
	}
//-->
</script>
<!-- END common collapse script -->

