node.doctext:
[%

return unless $query->param("verify") eq 'foo'; 



my $emailforwards = getNode('everything email forwarders', 'usergroup')->{group};

push @$emailforwards, @{ getNode('gods', 'usergroup')->{group}};
my $str;

my %crossover;



foreach (@$emailforwards) {
  next if $crossover{$_};
  $crossover{$_} = 1;
  #prevent duplicate entries

  my $U = getNode($_);
  next unless $$U{email};

  my $email = $$U{title};
  $email =~ s/\ /_/g;
  $str.= "$email\@everything2.com:\t$$U{email}\n";
}

$str;

%]
