node.doctext:
[%

my $str;
use XML::Generator;
my $XG = new XML::Generator;
use Everything::XML;
my $time=180;

my $curUserID = $$USER{node_id};
my $userID;

my $wherestr;

unless ($$VARS{infravision}) {
  $wherestr.=' and ' if $wherestr;
  $wherestr.='visible=0';
}

#return $wherestr.'hey!';
my $csr = $DB->sqlSelectMany('*', 'room', $wherestr, 'order by experience DESC');


my $lines;
my @users;
while (my $N = $csr->fetchrow_hashref) {
  push @users, $N;
}

$str.=$XG->INFO({site => $HTMLVARS{site_url}, sitename => $HTMLVARS{site_name},  servertime => scalar(localtime(time))}, 'Rendered by the Other Users XML Ticker');

my %rooms;
$rooms{0} = 'outside';
foreach my $N (@users) {

$userID = $$N{member_user};
  if (not $rooms{$$N{room_id}}) {
    my $ROOM = getNodeById($$N{room_id});
    $rooms{$$N{room_id}} = $$ROOM{title}; 
  }
  
  $str.="\n\t".$XG->user({room =>$rooms{$$N{room_id}}, user_id => $$N{member_user},username=>Everything::XML::makeXmlSafe($$N{nick})},"\n");

}


"\n".$XG->OTHER_USERS($str."\n");

%]
