node.code:
#originally in [chatterbox] (nodelet)

#pass which information types to show
#supported things to show information about:
#  sent - previous thing user sent
#  help - link to chatter FAQ

my @things = @_;
my $str;
my $info;

#TODO: split these up, since they aren't related at all
foreach $info (@things) {
if($info eq 'sent') {

if(defined $query->param('sentmessage')) {
	my $told = $query->param('sentmessage');
	my $i=0;
	while(defined $query->param('sentmessage'.$i)) {
		$told.="<br />\n".$query->param('sentmessage'.$i);
		++$i;
	}
	$told=parseLinks($told,0) unless $$VARS{showRawPrivateMsg};
	$str.='<p style="border:dashed black 1px;padding:2px;font-family:sans-serif;"><small>'.$told."</small></p>\n";
}
#if(my $told = $query->param('sentmessage')) {
#	$told = parseLinks($told,0) unless $$VARS{'showRawPrivateMsg'};
#	$str.='<p style="border:dashed black 1px;padding:2px;font-family:sans-serif;"><small>'.$told."</small></p>\n";
#}

} elsif($info eq 'help') {

use Everything::Experience;
$str .= '<p align="center"><small>'.linkNodeTitle('E2 FAQ: Chatterbox|what is this?')."</small></p>\n" if getLevel($USER)<2;

}
}

$str.'<p><small>(chatterinfo is deprecated)</small></p>';
