node.code:
my $POLL = shift;
getRef $POLL;
return 0 unless $POLL && $$POLL{type}{title} eq 'e2poll';

parseCode($POLL -> {doctext}) =~ /<p>([^<]+)/;
return 0 unless $$POLL{question} = $1;

my $i = 0;
my $options;
while ($HTMLVARS{options} -> {$i}){
	$options .= $HTMLVARS{options} -> {$i++}."\n\n";
}

$$POLL{doctext} = $options;
$$POLL{multiple} = 0;

updateNode($POLL, -1);
