node.code:
my $str;
my $title = $query->param('node');
return '' unless $title;

$str .= '<p>I guess I could arrange it so that you could create "' . $title . '" as a...</p><ul>
';

my @aprtypes;

#my %approvedtypes;	#all approved types
my %nonFirstTypes;	#all approved types except one being forced first
foreach my $NODETYPE ($DB->getAllTypes()) {
#	if (canCreateNode($USER, $NODETYPE)) {
#		$approvedtypes{$$NODETYPE{title}} = $NODETYPE;
		$nonFirstTypes{$$NODETYPE{title}} = $NODETYPE;
		#I have to do it in this kinda strange way to remove dupes...
#	}
}
#return unless %approvedtypes;
return unless %nonFirstTypes;

#force certain nodetypes first
my @forceFirst = qw(e2node);	#qw(e2node document superdoc);
my %forceSave;
foreach(@forceFirst) {
	if(exists $nonFirstTypes{$_}) {
		$forceSave{$_} = $nonFirstTypes{$_};
		delete $nonFirstTypes{$_};
		push(@aprtypes, $_);
	}
}

push(@aprtypes, sort {$a cmp $b} keys(%nonFirstTypes));

#my %description = (
#	'e2node' => 'what writeups are in',
#	'document' => 'unrestricted HTML',
#	'superdoc' => 'unrestricted HTML plus ability to execute Everything engine code',
#);

my $lid = $query->param('lastnode_id') || 0;	#if this is undef, Perl complains when adding to the hash
foreach my $type (@aprtypes) {
	$str .= '<li>';
	$str .= '<big>' if exists $forceSave{$type};
	$str .= '<a href=' . urlGen({
		node => $title,
		type => $type,
		op => 'new',
		'lastnode_id' => $lid,
	}) .'>'.$type.'</a>';
	$str .= '</big>' if exists $forceSave{$type};
#	$str .= ' <small>('.$description{$type}.')</small>' if exists $description{$type};
	$str .= "\n";
}

$str.'</ul>';
