node.code:
my @months = qw(January February March April May June July August September October November December);

my ($sec,$min,$hour,$mday,$mon,$year,$wday,$yday,$isdst) =
        gmtime(time);

$year+= 1900;

my $daydate = "$months[$mon] $mday, $year";
# Link to monthly ed log instead, since the dailies are extinct
my $mnthdate = $months[$mon].' '.$year;

my $logarchive = getNode("Log Archive","superdoc","light");

# Create daylog e2node if it's not already there.
my $daylog = getNode($daydate, 'e2node');

if (!$daylog) {

  local $USER = getNode('Cool Man Eddie', 'user');
  local $query = CGI->new;
  $query->param('node', $daydate);
  $query->param('type', 'e2node');
  $query->param('op', 'new');
  opNew();

}

parseLinks(
"<ul class=\"linklist\">
<li class=\"loglink\">[$daydate|Day logs for $daydate]</li>
<li class=\"loglink\">[Editor Log: $mnthdate|Editor logs for $mnthdate]</li>
<li class=\"loglink\">[root log: $mnthdate|Coder logs for $mnthdate]</li>
<li class=\"loglink\">".linkNode($logarchive)."</li>
</ul> "
) ;
