node.code:
my ($user_id) = @_;
return unless $user_id;
my $limit = 10;

use JSON;
my $str;


my ($csr) = $DB->sqlSelectMany("node_id, doctext", "node inner join document on node.node_id =document.document_id","node.author_user = $user_id and node.type_nodetype=1988675 order by node_id desc limit 50");

while (my $feedItem = $csr->fetchrow_hashref) {

	$str .= "<div class='feeditem' id='feeditem_".$$feedItem{node_id}."' style='padding: .5em; margin-bottom: .2em'>".$$feedItem{doctext}."</div>\n";
}

$str = 
"<div id='feed_list' style='margin-left: 0; padding-left: 0; font-size: 90%;'>".$str. "</div>";

if ($user_id == $$USER{user_id}) {
$str = "
<h4>Add feed item</h4>
<textarea name='new_feed_item' id='new_feed_item'></textarea>
<br />
<input type='button' onclick='E2AJAX.addFeedItem(\$F(\"new_feed_item\"))' value='Add' />
" .$str;
}

$str;
