node.code:
#displaywriteuptitle - pass the writeup's node_id and timestamp

my ($num, $timestamp) = @_;

my $WRITEUP;
my $votenum = 0;
if (not $num) {
	$WRITEUP = $NODE;

} else {
	my (@group) = @{ $$NODE{group} } if ($$NODE{group});
	@group or return;
	return if $num > @group;
	$WRITEUP = getNodeById($group[$num-1]);
}

	$votenum = getId($WRITEUP);

unless ($$WRITEUP{author_user}==$$USER{node_id}||htmlcode('isSpider')||$query->param('op'))
	{ $DB->sqlUpdate ("node", { -hits => 'hits+1' }, "node.node_id=$$WRITEUP{node_id}"); } 

return htmlcode('zenDisplayWriteupInfo',$votenum);
