node.code:
my $tab = htmlcode('getTab','');

my $nodeletStr = '';

my $UID = $$USER{user_id};
my $isAdmin = exists $HTMLVARS{'group_gods'}{$UID};
my $isCE = $isAdmin || exists $HTMLVARS{'group_content editors'}{$UID};

if ($isCE) {
$nodeletStr .= insertNodelet(getNode("e3 Master Control","nodelet")->{node_id}); 
}

if ($$NODE{title} eq 'Scratch Pads Drafts') {
$nodeletStr .= insertNodelet(getNode("E3 Help","nodelet"));
return $nodeletStr;
}


if ($tab eq "home") {
$nodeletStr .= insertNodelet(1930708);
}

if ($tab eq "chat") {
$nodeletStr .= insertNodelet(91);
}

if ($tab eq "read") {
$nodeletStr .= insertNodelet(1988635);
}

if ($tab eq "write") {
$nodeletStr .= insertNodelet(1988648);
}

if ($tab eq "profile") {
$nodeletStr .= insertNodelet(1988878);}

return $nodeletStr;
