node.code:
return '' unless canReadNode($$USER{user_id}, $NODE);

my $link;
if($query->param('displaytype') eq 'edit'){
  $link = linkNode($NODE,'click here to display changes',{op => 'unlock'});
}else{
  $link = linkNode($NODE,'click here to edit this information',
                   {'displaytype'=>'edit'});
}
return '<div style="text-align:center;font-weight:bold;margin:20px;">'.
       "( $link )</div>";
