#return '';

# Create a 'format' button for a named element.

my ($name) = @_;

# Render the button. Does so in javascript, purely to
# avoid showing a useless button
# in a browser with no javascript.

my $finalstr = '<script language="JavaScript" src=';

$finalstr.= urlGen({"node"=>"AutoFormat Javascript", "type"=>"rawdata", "displaytype"=>"raw" });

$finalstr.='>
</script>
<script><!--
document.write("<input type=\'button\' onClick=\'autoFormat(\"'.$name.'\")\' value=\'format\' id=\'formatButton\'>");
// --></script>';

return $finalstr;
