node.code:
use Everything::Room;
my $entrance= "0";
my $str = "";
my $canEnter = htmlcode('canEnterRoom');

if ($$canEnter{canEnter}) {
  $entrance=1;
  changeRoom($USER, $NODE);
} else {
  $str .= "<reason>".$$canEnter{explanation}."</reason>\n";
  $str .= "<warning>".$$canEnter{warning}."</warning>\n";
}
$str .= "<canenter>".$entrance."</canenter>\n";
$str .="<description>".encodeHTML(($query->param("links_noparse"))?($$NODE{doctext}):(parseLinks($$NODE{doctext})))."</description>";
$str;
