node.code:
# Returns the star rating for a node that the current user/Guest-User-session previously set.
# Returns the rating (1-10) or undef if there has been no rating by this user.
my ($ratedNode) = shift;

getRef($ratedNode);

my $sessionId = htmlcode('getStarRatingSessionId');
my $queryStr = <<SQLEND;
SELECT rating_value
  FROM starrating
  WHERE starrating_id = ?
  AND rater_test_session_id = ?
SQLEND

return $DB->getDatabaseHandle()->selectrow_array (
  $queryStr
  , { }
  , $$ratedNode{node_id}, $sessionId
  );
