node.code:
my ($ad_location) = @_;
my $node_id = $$NODE{node_id};
return "" if $node_id == 1369958; #stupid pot node that got us busted
return "" if $$NODE{type}{title} eq 'writeup' && $$NODE{parent_e2node} == 1369958;

return "" if $node_id == getNode('Nothing Found', 'superdoc')->{node_id};
return "" if $node_id == getNode('Findings:', 'superdoc')->{node_id};
return "" unless ($node_id =~ /^\d+$/);
return "" if $query->url =~ /^https/;

my ($ad_width, $ad_height, $ad_format, $ad_type);

if ($ad_location eq 'nodelet') {

  $ad_width =  160;
  $ad_height = 600;
  $ad_format = "160x600_as";
  $ad_type = "text_image";

# default case, 'topbanner'
} else {

  $ad_width =  728;
  $ad_height = 90;
  $ad_format = "728x90_as";
  $ad_type = "text_image";

}

my $ad = <<ADTEXT;
<center>
<script type="text/javascript">
google_ad_client = "pub-3290994517499171";
google_ad_slot = "7754250909";
google_ad_width = $ad_width;
google_ad_height = $ad_height;
google_ad_format = "$ad_format";
google_ad_type = "$ad_type";
google_ad_channel = "";
google_page_url = "http://everything2.com/?node_id=$node_id";
</script>
<script type="text/javascript"
  src="http://pagead2.googlesyndication.com/pagead/show_ads.js">
</script>


</center>
ADTEXT

return $ad;
