node.code:
my ($achievement, $user_id) = @_;

$user_id ||= $$USER{user_id};
return unless getNodeById($user_id)->{type_nodetype} = 15;

my $ACH = 0;
$ACH = getNode($achievement,"achievement");
$ACH ||= getNodeById($achievement);

return 0 unless $ACH;

my $achieved = $DB->sqlSelect("count(*)","achieved","achieved_user=$user_id and achieved_achievement=".$$ACH{node_id}." limit 1");
return 1 if $achieved;

my $codeStr = "
[%
my \$user_id = ". $user_id.";

".$$ACH{code}."

%]";

my $result = parseCode($codeStr);

if ($result == 1) {
	$DB->sqlInsert("achieved",{achieved_user => $user_id, achieved_achievement => $$ACH{node_id}});

	use JSON;
	my $notification = getNode("achievement","notification")->{node_id};
	if ($$VARS{settings}) {
		if (%{ from_json($$VARS{settings})->{notifications} }->{$notification}) {
			my $argSet = { achievement_id => $$ACH{node_id} };
			my $argStr = to_json($argSet);
			my $addNotifier = htmlcode('addNotification', $notification,$user_id,$argStr);
		}
	}
}

return $result;
