node.code:
# let page inject stuff into the header:
my ($str,$after) = split /<!--.*?-->/, (delete $HTMLVARS{pageheader})
	if $HTMLVARS{pageheader};

my $ntypet = $$NODE{type}{title};
if ( $ntypet eq 'e2node' or $ntypet eq 'writeup' ) {
	$str .= htmlcode( 'createdby' )."\n" if $ntypet eq 'e2node' ;
	$str .= htmlcode( 'firmlinks' )."\n" ;
	$str .= htmlcode( 'usercheck' )."\n" if $ntypet eq 'e2node' ;
}

if ($ntypet eq 'e2node' || $ntypet eq 'superdoc' || $ntypet eq 'superdocnolinks' || $ntypet eq 'document') {
	my $COOLLINK = getNode('coollink','linktype') -> {node_id};
	$HTMLVARS{edcoollink} = $DB->sqlSelectHashref('to_node', 'links', 'from_node='.$$NODE{node_id}.' and linktype='.$COOLLINK.' limit 1') ;
	$str .= '<div id="cooledby"><strong>cooled by</strong> '.linkNode($HTMLVARS{edcoollink}->{to_node})."</div>\n"
		if $HTMLVARS{edcoollink} ;
}

$str .= htmlcode( 'confirmop' ) if $query -> param( 'confirmop' ) ;
my $disabled = htmlcode('getdisabledactions');
$str .= htmlcode('page actions', $disabled) unless $$USER{user_id} == $HTMLVARS{guest_user} ;

unless ($disabled =~ /a/) {
    $str .= htmlcode('listnodecategories') unless defined delete $HTMLVARS{e2nodeCategories}; # i.e. unless writeups have already listed any page categories
}

return qq'<div id="pageheader">
	<h1 class="nodetitle">$$NODE{title}</h1>\n\t'.
	htmlcode( 'show_node_forward' ) . "
	$str
	$after
</div>" ;
