node.code:
my ($SETTINGNODE) = getNode('system settings',  getType('setting'));
my $SETTINGS = getVars $SETTINGNODE;
my ($HTMLVARS) = getNode('htmlvars nodegroup', getType('nodegroup'));
my (@htvars) = @{ $$HTMLVARS{group} };
my $OLDSETTINGS =
{
not_found => $$SETTINGS{notFound_node},
permission_denied => $$SETTINGS{permissionDenied_node},
search_group => $$SETTINGS{searchResults_node},
duplicate_group => $$SETTINGS{duplicatesFound_node},

node_locked => $$SETTINGS{nodeLocked_node}} ;

@$SETTINGS{keys %$OLDSETTINGS}= values %$OLDSETTINGS;

my @groups = ("gods", "content editors", "everything editors", "users with cool", "edev", "e2contact", "chanops", "sigtitle");

foreach my $usergroup (@groups) {
  my $G = getNode($usergroup, 'usergroup');
  $DB->groupCache($G, $$G{group}, "plain");
  $$SETTINGS{lc("group_".$usergroup)} = $DB->getGroupCache($G);
}
my $GUEST_LINK = getNode('guest user link', 'linktype');
$$SETTINGS{guest_link} = $GUEST_LINK if $GUEST_LINK;

$SETTINGS;
