node.code:
my ($node_id,$readonly,$num,$levels,$split,$showRating)=@_;
$node_id=$$NODE{node_id} unless ($node_id);
$num=$node_id unless ($num);
$levels=10 unless ($levels);
$split=2 unless ($split);
my $str='<div class="stars">';

my $currentRating=0;
if ($showRating) {
  $currentRating=$showRating;
}
else {
  $currentRating=int(htmlcode('getAverageStarRating', $node_id)+0.5);
}
for (my $count=1; $count<=$levels; $count++) {
  $str.="<input name='star$num' type='radio' class='star";
  $str.=" {split:$split}" if ($split);
  $str.="' checked='checked" if ($count==$currentRating);
  $str.="' value='$count";
  $str.="' disabled='disabled" if $readonly;
  #$str.="' onClick='e2.ajax.starRateNode($node_id, $count); alert();";
  $str.="' />";
}
$str.='</div>';
return $str;
