node.code:
$$VARS{fxDuration} = '1' if (delete $$VARS{notransitions});

my $lastnode = $$NODE{node_id};
$lastnode = $$NODE{parent_e2node} if $$NODE{type}{title} eq 'writeup';
$lastnode = $query->param("lastnode_id")||0
	if $$NODE{title} eq 'Findings:' && $$NODE{type}{title} eq 'superdoc';

my $e2 = "node_id: $$NODE{node_id}";
$e2 .= ", lastnode_id: $lastnode";
$e2 .= ', ignoreMissingLibraries: 1' if $HTMLVARS{ignoreMissingJsLibraries};

my $cookie;
foreach ('fxDuration', 'collapsedNodelets', 'settings_useTinyMCE', 'autoChat', 'inactiveWindowMarker'){
	if ($$USER{title} ne 'Guest User'){
		$$VARS{$_} = $cookie if $cookie = $query -> cookie($_);
		delete $$VARS{$_} if $cookie eq '0';
	}
	if ($$VARS{$_}){
		my $quot = '"' if $$VARS{$_} ne int $$VARS{$_};
		$e2 .= ", $_: $quot$$VARS{$_}$quot";
	}
}

my $min = '.min' unless $HTMLVARS{isDevServer};
my $libraries = qq'<script src="http://code.jquery.com/jquery-1.4.4$min.js" type="text/javascript"></script>';
# mark as guest but only in non-canonical domain so testing and caching both work
my $compilerPath = '/js/GuestJavascript.js';

unless ($$USER{title} eq 'Guest User'){
	$libraries .= '
	<script src="/js/jquery-ui.min.js" type="text/javascript"></script>';
	$compilerPath = '/node/jscript/Javascript+Compiler?a=9' ; # change this to force update
}

$libraries .= <<LIBS;
	<script src="/js/jquery.ba-bbq$min.js" type="text/javascript"></script>
LIBS

$libraries .= <<DEVLIBS . htmlcode('nagger') if $HTMLVARS{isDevServer};
	<script src="/stars/jquery.MetaData.js" type="text/javascript"></script>
	<script src="/node/jscript/star+script" type="text/javascript"></script>
DEVLIBS

return <<ENDJS;
	<script type='text/javascript'>
		e2 = {$e2};
	</script>
	$libraries
	<script src='$compilerPath' type='text/javascript'></script>
ENDJS
