node.code:
#section of the Statistics nodelet that displays site global stats

#puts commas into numbers
local *commify = sub {
	local $_  = shift;
	return 0 unless defined $_;
	1 while s/^([-+]?\d+)(\d{3})/$1,$2/;
	return $_;
};

my $stats = getNode('Statistics Data','nodelet')->{nltext};
my %values;
my $str;
foreach(split("\n", $stats)) {
	my ($key, $val) = split(': ', $_);
	$val = commify($val);
	$str.='<div><span class="var_label">'.$key.': </span><span class="var_value">'.$val."</span></div>\n";
}



return '<div>'.$str.'</div>';
