node.code:
my ($shownumbers, $isxml, $newwuonly) = @_;

return if $$USER{user_id} == $HTMLVARS{guest_user};

$$VARS{oldexp} = $$USER{experience} unless($$VARS{oldexp});
return  if ($$VARS{oldexp} == $$USER{experience} and not $newwuonly);
my $newexp = $$USER{experience} - $$VARS{oldexp};
$$VARS{oldexp} = $$USER{experience};

use Everything::Experience;

my $lvl = getLevel($USER)+1;

my $levelExperience = getVars(getNode('level experience', 'setting'))->{$lvl};
my $expleft = $levelExperience - $$USER{experience} if $levelExperience;

my $levelWriteups = getVars(getNode('level writeups', 'setting'))->{$lvl};
my $wrpleft;

$$VARS{numwriteups} ||= 0;

if (($$VARS{settings_useHonorRoll}) || ($$USER{user_id} != 1312198)) {
use POSIX;
$wrpleft = ceil((($levelWriteups * getHRLF($USER)) - $$VARS{numwriteups})) if $levelWriteups;
}
else {
$wrpleft = ($levelWriteups - $$VARS{numwriteups} - $$VARS{writeupbonus}) if $levelWriteups;
}


if (!$isxml) {
my $str;
$str.= ($newexp > 0 ? "You gained $newexp" : "Ack! You lost ". (-1 * $newexp) ) .
'<strong>'.$newexp.'</strong> experience point'.($newexp > 1 ? "s" : "").'!' unless $newwuonly;
$str.= '<br />You need <strong>'.$expleft.'</strong> more XP to earn level '.$lvl.'.' if $expleft > 0;
$str.= '<br />You need <strong>'.$wrpleft.'</strong> more writeups to earn level '.$lvl.'.' if $wrpleft > 0;
return $str;
}

my $xmlstr = "<xpinfo>
<xpchange value=\"$newexp\">$$USER{experience}</xpchange>
<nextlevel experience=\"$expleft\" writeups=\"$wrpleft\">$lvl</nextlevel>
</xpinfo>";
return $xmlstr;
