node.code:
my $str="";

my ($nostartform) = @_;

my $uid = getId($USER);
my $daylog = 0;

my @group = @{ $$NODE{group} } if $$NODE{group};
my $MYWRITEUP;
foreach(@group) {
	getRef($_);
	if($$_{author_user} == $uid) {
		$MYWRITEUP = $_;
		last;
	}
}


$MYWRITEUP ||= {doctext => '', title => '', node_id => 0};
#so we can use them as default values if the writeup was not found

my $NODELOCK = htmlcode('nodelockreason', $$MYWRITEUP{node_id});
return '<big>'.$NODELOCK.'</big>' unless($NODELOCK eq '');

if($uid == $HTMLVARS{guest_user}) {
	my $LOGIN = getNode 'login', 'superdoc';
	my $CREATEUSER = getNode 'create a new user', 'superdoc';  

	$str = 'Y\'know, if you '.linkNode($LOGIN,"log in").', you can write something here, or contact authors directly on the site. '.linkNode($CREATEUSER).' if you don\'t already have an account.';

	return $str if exists $$THEME{noThinOutline};
	return containHtml('thin outline container', $str);
}

if ($query->param("tinyMCE")) {
	if ($query->param("tinyMCE") == 1) {$$VARS{settings_useTinyMCE} = 1;}
	if ($query->param("tinyMCE") == -1) {$$VARS{settings_useTinyMCE} = 0;}
}

$str.= htmlcode('script_tinyMCE',"writeup_doctext");


#$str.=$query->start_form unless $nostartform;





$str.='<form method="post" action="http://everything2.com/index.pl" enctype="multipart/form-data" name="wusubform">' unless $nostartform;

my $node_id;
if($node_id=getId($MYWRITEUP)) {
	#the user is editing their old writeup

	#user doesn't want to see their text
	return '<br />'.linkNode($MYWRITEUP, $$MYWRITEUP{title}).' has your contribution to this node. You can edit it there.'.$str.=$query->end_form if($$VARS{HideWriteupOnE2node}); 

	#$str.= '<input type="hidden" name="node_id" value="'.$node_id.'">';
	$str.=htmlcode('setwriteuptype', $$MYWRITEUP{wrtype_writeuptype});
	$str.='<h2>Edit your writeup</h2>';
} else {
#	offer the user the chance to make a new one.
	$str.="
\t\t<h2>Enter a goddamn writeup</h2>
\t\t<input type='hidden' name='type' value='writeup'>";

	my $curTitle = $$NODE{title};
	my $checkIt = 0;

	if( $$VARS{HideNewWriteups} || ($curTitle=~/^(?:dream|editor|root) Log: /i) ) {
#	if( $$VARS{HideNewWriteups} || ($curTitle=~/^(dream|editor|root) Log: /i) ) {
		$daylog = 1; # for the purposes of determining whether the 'personal' writeuptype is enabled
		$checkIt=1;
	} elsif($curTitle =~ /^(?:January|February|March|April|May|June|July|August|September|October|November|December) [1-9]\d?, \d+/i) {
#	} elsif($curTitle =~ /^(January|February|March|April|May|June|July|August|September|October|November|December) [1-9]\d*, \d+/i) {
		$daylog = 1;
		$checkIt=1;
	} else {
		my $unVotable = getVars(getNode('boring heaven nodes', 'setting'));
		my $t = $curTitle;
		$t =~ s/\s/_/g;
		$checkIt=1 if $$unVotable{lc($t)};
	}

	$str.="\n\t\t".'<input type="checkbox" name="writeup_notnew" value="1"';
	$str.=' checked="1"' if $checkIt;
	$str.='>(Don\'t display in "New Writeups")';

	if (getLevel($USER) == 1) {
		$str.="\n\t\t<p>Before entering your writeup, you might want to post it to your <b>" . linkNode(getNode('Scratch Pads','superdocnolinks'), 'Scratch Pad')."</b> first. By posting it to your Scratch Pad, you can see how it looks and give other noders a chance to read, edit, and critique your writing - without having to suffer any downvotes or backlash because of poor formatting or silly typos.</p>";
		$str.="\n<p>Writeups should include ".linkNode(getNode('Links on Everything2','e2node'),'links').", like this: &#91;".linkNode(getNode('hard link','e2node'))."&#93; &#91;".linkNode(getNode('pipe link','e2node'),'target|pipe link')."&#93; &#91;http://example.com|external link&#93;</p>"
	}
	if (!($$VARS{settings_useTinyMCE})) {
		$str.="\n <p>".linkNode(getNode('E2 HTML tags','e2node')).": &lt;<abbr title='bold - you should usually use strong instead'>b</abbr>&gt; &lt;big&gt; &lt;blockquote&gt; &lt;<abbr title='line break'>br</abbr> /&gt; &lt;cite&gt; &lt;code&gt; &lt;<abbr title='definition description'>dd</abbr>&gt; &lt;<abbr title='deleted'>del</abbr>&gt; &lt;<abbr title='definition list'>dl</abbr>&gt; &lt;<abbr title='definition title'>dt</abbr>&gt; &lt;<abbr title='emphasis'>em</abbr>&gt; &lt;<abbr title='heading 1'>h1</abbr>&gt; &lt;<abbr title='heading 2'>h2</abbr>&gt; &lt;h3&gt; &lt;h4&gt; &lt;h5&gt; &lt;h6&gt; &lt;<abbr title='horizontal rule'>hr</abbr> /&gt; &lt;hr width=\'??px\' /&gt; &lt;<abbr title='italics - in most cases em is better'>i</abbr>&gt; &lt;<abbr title='list item'>li</abbr>&gt; &lt;<abbr title='ordered (i.e. numbered) list'>ol</abbr>&gt; &lt;ol type=\'1/A/a/I/i\'&gt; &lt;<abbr title='paragraph'>p</abbr>&gt; &lt;p align=\'left/center/right/justify\'&gt; &lt;<abbr title='pre-formatted - monotype font, preserving spacing'>pre</abbr>&gt; &lt;<abbr title='quote'>q</abbr>&gt; &lt;small&gt; &lt;strong&gt; &lt;<abbr title='subscript'>sub</abbr>&gt; &lt;<abbr title='superscript'>sup</abbr>&gt; &lt;<abbr title='teletype - monospaced'>tt</abbr>&gt; &lt;<abbr title='unordered list (i.e. one with bullet points)'>ul</abbr>&gt; </p>\n<p>" .linkNode($$NODE{node_id},"Turn on WYSIWYG editing",{tinyMCE => 1})." if you prefer to see roughly how the writeup will look as you enter it, rather than entering HTML tags by hand. Note that you will lose the current contents of this text area.</p><p><a href=\"javascript:toggleEditor('writeup_doctext');\">Add/Remove editor</a></p>";
	}

	if (($$VARS{settings_useTinyMCE}) == 1) {
		$str.="<p> ".linkNode($$NODE{node_id},"Turn off WYSIWYG editing",{tinyMCE => -1})." if you prefer to edit HTML directly. Note that you will lose the current contents of this text area.</p><p><a href=\"javascript:toggleEditor('writeup_doctext');\">Add/Remove editor</a></p>";

	}

}

#$str.=$query->textarea('writeup_doctext', $$MYWRITEUP{doctext}, 20, 60, '', 'wrap=virtual').'<br />';

$str.= '<script type="text/javascript" src=';
$str.= urlGen({"node"=>"AutoFormat Javascript", "type"=>"rawdata", "displaytype"=>"raw" });
$str.='>\n</script>';

$str.= '<script  type="text/javascript" src=';
$str.= urlGen({"node"=>"HTMLtoolbar", "type"=>"rawdata", "displaytype"=>"raw" });
$str.='>\n</script>';



$str.="\n\n\t\t".'<script type="text/javascript">edToolbar(\'writeup_doctext\');
if (tinyMCE.getInstanceById(\'writeup_doctext\')) document.getElementById(\'ed_toolbar_writeup_doctext\').style.display = \'none\';</script>';
$str.="\n\n\t\t".'<textarea name="writeup_doctext" id="writeup_doctext" '.htmlcode('customtextarea', '1').' wrap="virtual">'.encodeHTML($$MYWRITEUP{doctext}).'</textarea><script type="text/javascript">var edCanvas = document.getElementById(\'writeup_doctext\');</script>';
#$str.="<br />".htmlcode('formatButton','writeup_doctext');
$str.="\n\t\t".'<input type="hidden" name="writeup_parent_e2node" value="'.getId($NODE)."\">";

if ($node_id) {
	$str.=$query->submit('sexisgood', 'sumbit');
} else {
	#we let them define the type

	my (@WRTYPES) = getNodeWhere({type_nodetype => getId(getType('writeuptype'))});
	my @WRTYPE;
	foreach(@WRTYPES) {
		push @WRTYPE, $_ unless ( (lc($$_{title}) eq 'definition') || (lc($$_{title}) eq 'lede'));
	}

	my $chwriteup = "
		<p><strong>Some Helpful Links:</strong>
		[E2 HTML Tags] &middot; 
		[HTML Symbol Reference] &middot; 
		[Using Unicode on E2] &middot;
		[Reference Desk]
		</p>


\t\t\t<input type='hidden' name='new_scratch_title' value='".$$NODE{title}."'>
\t\t\t<p><button type='submit' name='node_id' value='1797151'>Create scratch pad</button>
\t\t\t<input type='checkbox' name='new_scratch_private' value='1' /> Make it private
\t\t\t</p>
		<p><button type='submit' name='op' value='new' id='submitWriteup' title='this should disappear!'>Create writeup as a:</button> ";
	my $count=0;
	my $t;
	$chwriteup.="
		<select name='writeuptype'>";
	foreach(@WRTYPE) {
		$t = $$_{title};
		$chwriteup.="\n\t\t\t<option value='$t'>$t</option>";
		#$chwriteup.=$query->submit('writeuptype', $t);
		#$chwriteup.='</td><td align="center">' if ++$count < @WRTYPE;
	}
	$chwriteup.="
		</select>\n\t\t</p>";

	if(exists $$THEME{noThinOutline}) {
		$str.='<table width="100%" border="0" cellpadding="0" cellspacing="10"><tr><td>'.$chwriteup.'</td></tr></table>';
	} else {
		$str.="\n\n\t\t".containHtml('thin outline container', parseLinks($chwriteup));


	}
}

$str.=$query->end_form;
