my (@thingies) = @_;
my $r;
my $str = '';
foreach my $thingy (@thingies) {
	$r=ref($thingy);
	$str .= 'thingy "'.$thingy.'" is a '.$r;
	if($r eq 'HASH') {
		$str .= '; keys: '.join(', ',keys(%$thingy));
	}
	$str .= "<br />\n";
}
return $str;
