node.code:
my ($searchTerm) = @_;
# recover escaped commas from input
$searchTerm =~ s/\|COMMA\|/,/g;
$searchTerm =~ s/\|COMMACOMMA\|/|COMMA|/g;
$searchTerm = $DB->quote($searchTerm . '%');

my ($userType, $usergroupType) =
  map { getId(getType($_)) } ( 'user', 'usergroup');

my $names_cursor =
  $DB->sqlSelectMany(
    "title"
    , "node"
    , "(type_nodetype = $userType OR type_nodetype = $usergroupType)"
      . " AND title LIKE ${searchTerm}"
    , "LIMIT 10"
  );

my @possible_names = ();

while(my $name = $names_cursor->fetchrow)
{
  push @possible_names, $name;
}

my @completions =
  map
    { { label => $_, value => $_, id => $_ }; }
    @possible_names
    ;

return \@completions;
