node.code:
my $limit = 10;

use JSON;
my $str;


my ($csr) = $DB->sqlSelectMany("node_id, doctext", "node inner join document on node.node_id =document.document_id","node.author_user = $$USER{user_id} and node.type_nodetype=1988675 order by node_id");

my $feed_list = {};
my $count = 1;

while (my $feedItem = $csr->fetchrow_hashref) {

	$$feed_list{$$feedItem{node_id}} = 1;
	$$feed_list{$count} = {id => $$feedItem{node_id},  value => $$feedItem{doctext}};
	$count++;
}

return $feed_list;
