node.code:
my ($k, @title) = @_;

#inverse of varcheckbox
#	checked   : $$VARS doesn't exist
#	unchecked : $$VARS is 1

return if (getId($USER)==$HTMLVARS{guest_user}) || ($$USER{title} eq 'everyone') || ($$USER{title} eq 'Guest User');
my $title = join ', ', @title;
$title ||= $k;

if($query->param('unsetvar_'.$k)) {
	delete $$VARS{$k};
} elsif($query->param('sexisgood')) {
	$$VARS{$k} = 1;
}

$query->checkbox('unsetvar_'.$k, !$$VARS{$k}, '1', $title);
