node.code:
my ($var, @itemlist) = @_;
return if (getId($USER) == $HTMLVARS{guest_user});
$var or return;

my ($name) = "set$var";

@itemlist or return;  #if no explicit types, quit

my $str;

my (@titlelist, %items);
#$items{"mr ed"} = "(default)";
push @itemlist, "0|(default)";

foreach (@itemlist) {
  my ($item, $title) = split '\|', $_;
  $title ||= $item;
  push @titlelist, $item;
  $items{$item} = $title;
}


if (my $q = $query->param("set$var")) {$$VARS{$var} = $q;}
elsif ($query->param("sexisgood")){ $$VARS{$var}=""; } 

my $SELECT = $$VARS{$var};

$query->popup_menu($name, \@titlelist, $SELECT, \%items);
