node.code:
my ($queryid, $N) = @_ ;
getRef $N ;
$N ||= $NODE ;

my $msg = $query->param($queryid) ;
my $wutitle = $$N{ title } ;
$wutitle =~ s/ \(\w+\)$// ;

my $msgreport = htmlcode('sendPrivateMessage', {
	'recipient_id'=>$$N{ author_user },
	'message'=>$msg,
	'ccself'=>( $query->param('cc'.$queryid) ? 1 : 0 ) ,
	'renode'=>$wutitle
});

if( $msgreport ) {
	$msgreport = ' <strong>Error</strong>: unable to send writeup message "'.$msg.'": '.$msgreport ;
} else {
	$query -> Delete($queryid);
	$msgreport = $msg;
	$msgreport =~ s/\</&lt;/g;
	$msgreport =~ s/\>/&gt;/g;
	htmlcode('addNodenote', $query -> param("nn$queryid"), qq'messaged: "$msgreport"', $USER)
		if $query -> param("nn$queryid");
	$msgreport = '<strong>Sent writeup message: </strong>'.parseLinks( $msgreport , $$N{parent} ) ;
}

qq'<p id="sent$queryid" class="sentmessage">$msgreport</p>'
