node.code:
my ($wuID) = @_;
my $wu = getNodeById($wuID);

my $wuHeader = '';

$wuHeader .= "

	<div class='writeup_info' id='writeup_$wuID'>
<div style='float: left'>
		submitted by<br /><span class='writeup_author'> ".linkNode($$wu{author_user})."</span><br />
		<span class='writeup_date'>$$wu{createtime}</span>
</div>
<div style='float: right'>
		<span class='writeup_message'>
			<a href='javascript:WU".$wuID.".writeupMessage()'>MSG</a>
		</span>
		<span class='writeup_bookmark' >".
			( $DB->sqlSelect('count(*)', 'links', "from_node=".$$USER{node_id}." and to_node=".$$NODE{node_id}." and linktype=".getId(getNode('bookmark','linktype'))) ? 
			"bookmarked" : 
			"<a title='bookmark this writeup' href='javascript:WU".$wuID.".bookmark()'>BOOK</a>"
			)."
		</span>
		<span class='writeup_favorite'>".
			( 0 == 1?
			"favorited" : 
			"<a href='javascript:WU".$wuID.".favorite()'>FAVE</a>"
			)."
		</span>
		";

my $isEditor = htmlcode('isEditor');
if ($isEditor) {
$wuHeader .= "
		<span class='writeup_kill'>".
			( 0 == 1? 
			"killed" : 
			"<a href='javascript:WU".$wuID.".killBox()'>KILL</a>"
			)."
		</span>
		<span class='writeup_toolset small_text'>
			<a href='javascript:WU".$wuID.".toolSet()'>Admin Toolset</a>
		</span>
		<span class='writeup_box' style='display:none'></span>";

$wuHeader.= "
</div>
<div style='clear:both'></div>
</div>
<script type='text/javascript'>
 var WU".$wuID.";
 Deferrer.defer(function() {WU".$wuID." = new WriteupAJAX(\$('writeup_".$wuID."'));}, 'writeupAjaxExists');
</script>";
}


$wuHeader;
