node.page:
[%
return (($$USER{user_id} == $$NODE{author_user})?('<p align="right">'.linkNode($NODE, 'edit', {'displaytype'=>'edit'}).'</p>'):(''));
%]
<p>
<table>
[%
my $usr = getNodeById($$NODE{author_user});

my $str = "<tr><td><strong>Maintainer:</strong></td><td>".linkNode($usr)."</td></tr>";
$str .= "<tr><td><strong>Homepage:</strong></td><td>[$$NODE{homeurl}]</td></tr>";
$str .= "<tr><td><strong>Download:</strong></td><td>[$$NODE{dlurl}]</td></tr>.";
$str .= "<tr><td><strong>Version:</strong></td><td>$$NODE{version}</td></tr>.";
$str .= "<tr><td><strong>Unique Client String:</strong></td><td><b>$$NODE{clientstr}</b></td></tr>";
return parseLinks($str);
%]

</table>
</p>
