node.page:
[%  
my $str = '';
if ($$NODE{enabled} == 0) {
  $str .= "This test is not running";
  if ($$NODE{starttime} ne "0000-00-00 00:00:00") {
     $str .= ", it last ran from $$NODE{starttime} to $$NODE{endtime}.";
  }else {  
     $str .= ".";
  }
} else {
  $str .= "This test has been running since $$NODE{starttime}";
}

$str .= "<p>". linkNode($NODE, "edit this test", { 'displaytype' => 'edit' });
%]
<p>
<b>Conditions</b>:
[%
  if (not $$NODE{conditions}) {
    return "no conditions have been specified.";
  }

  my @conditions = split ",", $$NODE{conditions};
  my $str;
  my $V = getVars($NODE);
  foreach my $cond (@conditions) {
    $str .= "<li>code blocks for $cond<ul>";
    if (not keys %$V) { $str .= "<li><i>no changes</i></li>"; }
    foreach my $key (keys %$V) {
       if ($key =~ /^$cond\|(.*)$/) {
         $str .= "<li>". linkNodeTitle($$V{$key}) . " replaces " . linkNodeTitle($1)
       }
    }
    $str .= "</ul>";
  }

  return $str;
%]
