node.page:
[%
use POSIX qw(ceil floor);

return unless isGod($USER);

my $open = "1\;";
my $locked = "0\;";

my $otherone;
my $title = "";
if ($$NODE{criteria} eq $open) {
  $title = "lock";
  $otherone = $locked;
} elsif ($$NODE{criteria} eq $locked) {
  $title = "unlock";
  $otherone = $open;
} else {
  return;
}

"<font size=1><i>".linkNode($NODE, $title, {room_criteria=>$otherone})."</i></font>";


%]
<p>

[%
use Everything::Room;

my $canEnter = htmlcode('canEnterRoom');

if (!$$canEnter{canEnter}) {
   my $errorMessage = $$canEnter{explanation};
   $errorMessage = "<strong style='color:red;'>$errorMessage</strong>"
      if $$canEnter{warning};
   return "<p>$errorMessage</p>";

} else {

  changeRoom($USER, $NODE);

  # For room usage counting:
  my (undef, undef, undef, $day, $mon, $year) = localtime();
  $NODE->{lastused_date} = join "-", ($year+1900,++$mon,$day);
  updateNode($NODE, -1);

  return "You walk into $$NODE{title}";

}

%]
<p>
[{parselinks:doctext}]
[%
  return "<br><p align=\"right\">(".linkNode(getNode("go outside", "superdocnolinks")).")</p>";
%]
