node.doctext:
var Debugger = {
	log: function (str) {
		if (!Debugger.enabled) {
			return;
		}
		if (!Debugger.window) {
			Debugger.showWindow();
		}
		Debugger.window.innerHTML += "LOG: " + str + "<br>";
	},
	showWindow: function () {
		Debugger.window = document.createElement('div');
		with (Debugger.window.style) {
			width = '400px';
			height = '200px';
			backgroundColor = '#ffffff';
			border = '1px black solid';
			position = 'fixed';
			bottom = '10px';
			left = '20px';
			overflow = 'auto';
		}
		$$('body')[0].appendChild(Debugger.window);
	},
	window: false,
	enabled: false	
} ;
