node.code:
my ($N) = @_;

use Everything::Experience;

my $canCreate = (getLevel($USER) >= $HTMLVARS{roomCreateLevel} or isGod($USER));
$canCreate = 0 if htmlcode('isSuspended', 'room', $USER);

if (!$canCreate) {
  nukeNode($N, -1);
  return;
}

getRef($N);
$$N{criteria} = "1\;";
$$N{author_user} = getId(getNode('gods', 'usergroup'));
$$N{doctext} = $query->param('room_doctext') if defined $query->param('room_doctext');

updateNode($N, -1);
