node.code:
return; # Disabled - 'Maintains' set to 'none' in 'Edit Node', more importantly.
my ( $STYLESHEET ) = @_;

getRef( $STYLESHEET );

if (length($$STYLESHEET{title})==0) {
nukeNode($STYLESHEET, -1); return;
}

if (length($query->param("style_text"))==0) {
nukeNode($STYLESHEET, -1); return;
}

$$STYLESHEET{doctext}=$query->param("style_text");
updateNode( $STYLESHEET, -1 );
