node.code:
my ($DELETED_USER) = @_;
getRef $DELETED_USER;

my $deleteNode = getNode('The Old Hooked Pole', 'restricted_superdoc');
if ($deleteNode) {
      $DB->sqlInsert('seclog', {
        'seclog_node'    => $$deleteNode{node_id},
        'seclog_user'    => $$USER{user_id},
        'seclog_details' => "Deleted user $$DELETED_USER{title} (node_id $$DELETED_USER{node_id})"
      });
}

# Remove user from room lists so [Other Users[nodelet]] doesn't bug out
$DB->sqlDelete('room', "member_user = $$DELETED_USER{node_id}");
