node.code:
my ($NODE) = @_;
getRef $NODE;

my $AUTH = getNodeById($$NODE{author_user});
my $V = getVars $AUTH;

my $E2NODE = getNodeById($$NODE{parent_e2node});

my $noexp = 0;

if($E2NODE){ #Yes, writeups can be messed up in many ways
  my $parentID = $$E2NODE{node_id};

  #Check for maintenance nodes
  foreach (values %HTMLVARS) {
    if ($parentID eq $_) {
      $noexp=1;
      last;
    }
  }
}

unless($noexp){
  $$V{numwriteups}--;
  $DB->sqlUpdate("user",
                 {numwriteups => $$V{numwriteups} },
                 "user_id=$$NODE{author_user}");
}

setVars($AUTH, $V);

updateNode($AUTH);

$DB->sqlDelete('vote', 'vote_id='.getId($NODE));
$DB->sqlDelete('newwriteup', 'node_id='.getId($NODE));

htmlcode('update New Writeups data');
