nodes in the cache:<br>
[%
my $str;
my $count=0;
my %pertype = ();
foreach (values %Everything::NODES) {
  if ($_ and ref $$_{type}) {

#comment this back in to get a complete list of loaded nodes
    #$str.="<li>".linkNode($_)."($$_{type}{title})";
    $pertype{$$_{type}{title}}++;	
  }else{
	$str .= "<li><font color=red>$$_{title}</font>" unless ref($$_{type});
  }
$count++;
}
$str.="<br>$count nodes in total";
foreach (keys %pertype) {
  $str.="<br>$pertype{$_} of $_";
}

$str;
%]
