node.nlcode:
[%
my $str = "";

#return "Nothing to see here, " . $$USER{title} . "... move along." unless ($$USER{user_id} eq $$NODE{author_user});

############################
# Some revised node bucket code
############################


my $CAT = getNode('category', 'nodetype');

if($$NODE{type_nodetype} == $$CAT{node_id})
	{
	return "" unless($$NODE{author_user} == $$USER{author_user});
	}

my $PARAMS = { op => 'addBook', "bnode_" . $$NODE{node_id} => 1 };
$str .= linkNode($NODE, "Add '$$NODE{title}'", $PARAMS) . "<BR>";

$str .= $query->start_multipart_form("POST", $ENV{script_name}) .
  $query->hidden("displaytype") .
  $query->hidden("node_id", $$NODE{node_id}) .
  $query->hidden(-name => "op", -default => "bookop", -override => 1);


my @bnodes;
@bnodes = split ',', $$VARS{bookbucket} if (defined($$VARS{bookbucket}));


my $isGroup = 0;


if($$NODE{type_nodetype} == $$CAT{node_id})
	{
	$isGroup = "nodegroup";
	}


unless (@bnodes)
{
  $str .= "No Unsorted Bookmarks<br>\n" unless(@bnodes);
}
else
{
  foreach $_ (@bnodes)
  {
    my $node = getNode $_;

    # Can't use CGI::checkbox here because it insists on having a label...
    $str .= "<INPUT TYPE='checkbox' NAME='bnode_$_' " .
      "VALUE='ON' UNCHECKED>";
    $str .= linkNode($node) . "<br>\n";
  }

  $str .= "<font size=1>\n";

  $str .= "<INPUT TYPE='checkbox' NAME='dropexec' VALUE='ON' CHECKED>" .
    "Execute and drop<br>\n" if($isGroup);

  if($isGroup)
  {
    $str .= $query->submit( -name => "bgroupadd", -value => "Add to Category") ."\n";
  }

  $str .= $query->submit( -name => "bdrop", -value => "Drop" ) . "\n";
  $str .= $query->submit( -name => "bflush", -value => "Drop All" ) . "\n";

  $str .= "</font>\n";
}

$str .= "</form>";





############################
# Hierarchies
############################


$str .= "
<style>
   BODY {background-color: white}
   TD {font-size: 10pt; 
       font-family: verdana,helvetica;}
</style>
<script src=\"ua.js\"></script>
<script src=\"ftiens4.js\"></script>

" . htmlcode("switch_text") . "

<a href=\"http://www.treemenu.net/\"></a>
<br><script>initializeDocument()</script><br>
<input type=\"button\" value=\"New Category\" onclick=\"addcat()\">
</form>";

$str;
%]
