return unless $xml;
my $rss=new XML::RSS();
eval '
$rss->parse($xml); ';
my $str = "<a href=\"http://everydevel.everything2.com/?node=everything+core\" target=\"_blank\">".$rss->{channel}->{description}."</a><br>
";
my $count;
foreach my $i( @{$rss->{items}}){
  last if ++$count > 5;
  $str.="<a href=\"$i->{link}\">$i->{title}</A><br>\n";
}
$rss=undef;
$str;

