node.nlcode:
[%

my $str='';

my $voted = $DB->sqlSelect(
	'pollvote_id'
	, 'e2poll JOIN pollvote ON e2poll_id = pollvote_id'
	, "is_dailypoll=1 AND voter_user=$$USER{node_id}"
);

$str.='<ul><li>';
$str.='<strong>' unless $voted;
$str.=linkNodeTitle('Everything User Poll');
$str.='</strong>' unless $voted;

$str.='</li><li>'.linkNodeTitle('Everything\'s Most Wanted');
$str.='</li><li>'.linkNodeTitle('Usergroup Picks').'</li></ul>';


return $str;

%]
