node.nlcode:
[%
     my $str='';

     #delete node function
     $str.="\n<SCRIPT language=\"javascript\">
       function confirmDelete(){
          if(confirm(\"Delete '$$NODE{title}'?\")){
		parent.location=" .
		urlGen({node_id=>getId($NODE),op=>"nuke"}) .
		"; }
        }
       </SCRIPT>\n";

     #add node to coolnode group
     my ($gr) = getNode('coolnodes','nodegroup');
     $str .= "<SCRIPT language=\"javascript\">
	function updateGroup() {
	window.open('" .
		urlGen({node_id => getId($gr),
			displaytype => 'editor',
			add => getId ($NODE)}, "noquotes") .
		"','". getId($gr) ."',
		'');    
     }
     </SCRIPT>\n";
     $str .= "<table width=98% border=0><tr>";

     if(canDeleteNode($USER,$NODE))
     {
        $str.= "<td align=center><a href='javascript: confirmDelete();'>
                  <img border=0 src=\"images/x.gif\"></a></td>";

     }
     if(canUpdateNode($USER,$gr))
     {
        $str.="<td align=center>
               <a href='javascript: updateGroup();'><img border=0 src='images/plus.gif'></a></td>"
     }
     $str;
%]
     <td align=center><img border=0 src='images/b.gif'></td>
     </tr></table>
     </font>
