node.nlcode:
[%
my $str = "<updated>";
my ($sec,$min,$hour,$mday,$mon,$year) = localtime;
$str .= sprintf("%04d-%02d-%02dT%02d:%02d:%02dZ", $year + 1900, $mon + 1, $mday, $hour, $min, $sec);

$str .= "</updated>\n";

my $numberofwriteups = 25;
my $contentslicelength = 1024 ;

my $nodeids = $dbh -> selectcol_arrayref(
	"SELECT node_id
	FROM newwriteup
	WHERE notnew=0
	ORDER BY newwriteup_id DESC limit $numberofwriteups" ) ;

while ( $contentslicelength > 0 ) {
	my $meat = htmlcode( 'atomiseNode' , $nodeids , $contentslicelength );
	return $str . $meat if length( $meat ) < 65000 ; # nltext can only hold 65535
	$contentslicelength -= 100 ;
}
return $str ;

%]
